import './App.css';
import { Calendar, dateFnsLocalizer } from "react-big-calendar";
import format from "date-fns/format"
import parse from "date-fns/parse";
import startOfWeek from "date-fns/startOfWeek"
import getDay from "date-fns/getDay";
import "react-big-calendar/lib/css/react-big-calendar.css";
import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css"

const locales = {
  "en-US": require("date-fns/locale/en-US")
}

const localizer = dateFnsLocalizer({
  format,
  parse,
  startOfWeek,
  getDay,
  locales
})

const myEvents = [
  {
    title : "Meeting",
    allDay : true,
    start : new Date(2022,2,1),
    end : new Date(2022,2,2)
  },
  {
    title : "Vacation",
    start : new Date(2022,2,7),
    end : new Date(2022,2,10)
  },
  {
    title : "Conference",
    start : new Date(2022,2,8),
    end : new Date(2022,2,8)
  },

]

function App() {
  const [newEvent, setNewEvent] = useState({title : "", start : "", end : ""})
  const [allEvents, setAllEvents] = useState(myEvents)

  function handleAddEvent() {
    setAllEvents([...allEvents,newEvent])
  }

  return (
    <div className="App">
    <h1>Student Calendar</h1>
    <h2>Add New Event</h2>
    <div>
    <span>
      <input
        type="text"
        placeholder="Add Title"
        style={{width : "20%", marginRight : "10px"}}
        value={newEvent.title}
        onChange={(e) => setNewEvent({...newEvent, title: e.target.value})}
      />
    </span>
    <span>
      <DatePicker
        placeholderText="Start Date"
        style={{marginRight : "10px"}}
        selected={newEvent.start}
        onChange={(start) => setNewEvent({...newEvent, start : start})}
      />
    </span>
    <span>
      <DatePicker
        placeholderText="End Date"
        selected={newEvent.end}
        onChange={(end) => setNewEvent({...newEvent, end : end})}
      />
    </span>
      <button style={{marginTop: "10px"}} onClick={handleAddEvent}>
        Add Event
      </button>
    </div>

    <div>
      Days of the Week
      <div>
        <span>
          <label for="Monday">Mo</label>
          <input type="checkbox" name="Monday" id="Monday"/>
        </span>
        <span>
          <label for="Tuesday">Tu</label>
          <input type="checkbox" name="Tuesday" id="Tuesday"/>
        </span>
        <span>
          <label for="Wednesday">We</label>
          <input type="checkbox" name="Wednesday" id="Wednesday"/>
        </span>
        <span>
          <label for="Thursday">Th</label>
          <input type="checkbox" name="Thursday" id="Thursday"/>
        </span>
        <span>
          <label for="Friday">Fr</label>
          <input type="checkbox" name="Friday" id="Friday"/>
        </span>
        <span>
          <label for="Saturday">Sa</label>
          <input type="checkbox" name="Saturday" id="Saturday"/>
        </span>
        <span>
          <label for="Sunday">Su</label>
          <input type="checkbox" name="Sunday" id="Sunday"/>
        </span>
      </div>
    </div>
      <Calendar
      localizer={localizer}
      events={allEvents}
      startAccessor="start"
      endAccessor="end"
      style ={{height : 720, margin : "50px"}} />
    </div>
  );
}

export default App;
